scan_table_check.script:
/* global engine, GlideAggregate */
/* eslint no-undef: "error" */
(function (engine) {

	var numLanguages = (function () {
		var languageGa = new GlideAggregate('sys_language');
		languageGa.addQuery('active', true);
		languageGa.addAggregate('COUNT');
		languageGa.query();
		if (languageGa.next()) {
			return +languageGa.getAggregate('COUNT');
		}

		return 0;
	})();

	if (numLanguages > 1) {
		var listControlGr = engine.current;
		var translatedGa = new GlideAggregate('sys_translated');
		translatedGa.addQuery('name', 'sys_ui_list_control');
		translatedGa.addQuery('element', 'label');
		translatedGa.addQuery('value', listControlGr.label);
		// can be theoratically bypassed by having a single translation appearing multiple times 
		translatedGa.addAggregate('COUNT');
		translatedGa.query();
		if (translatedGa.next()) {
			var numTranslatedFields = +translatedGa.getAggregate('COUNT');
			if (numTranslatedFields != numLanguages - 1) {
				engine.finding.increment();
			}
		} else {
			engine.finding.increment();
		}
	}

})(engine);
