scan_table_check.script:
/* global engine, GlideAggregate */
/* eslint no-undef: "error" */
(function (engine) {

	var numLanguages = (function () {
		var languageGa = new GlideAggregate('sys_language');
		languageGa.addQuery('active', true);
		languageGa.addAggregate('COUNT');
		languageGa.query();
		if (languageGa.next()) {
			return +languageGa.getAggregate('COUNT');
		}

		return 0;
	})();

	if (numLanguages > 1) {
		var dictionaryGr = engine.current;
		var documentationGr = new dictionaryGr('sys_documentation');
		documentationGr.addQuery('name', documentationGr.name);
		documentationGr.addQuery('element', dictionaryGr.element);
		documentationGr.addAggregate('COUNT');
		documentationGr.query();
		if (documentationGr.next()) {
			var numTranslated = +documentationGr.getAggregate('COUNT');
			if (numTranslated != numLanguages) {
				engine.finding.increment();
			}
		} else {
			engine.finding.increment();
		}
	}

})(engine);
